parameters.jsCode:
// Since you already have 4 separate items from the previous code node
const allItems = $input.all();

// Extract SRT and TITLE from each item
const srtData = {};
const titleData = {};
const srtArray = [];
const titleArray = [];

allItems.forEach((item, index) => {
  const srt = item.json.subtitles && item.json.subtitles[0] 
    ? item.json.subtitles[0].srt 
    : '';

  const title = item.json.title || '';

  // Create numbered fields
  srtData[`srt_${index + 1}`] = srt;
  titleData[`title_${index + 1}`] = title;

  // Add to arrays
  srtArray.push(srt);
  titleArray.push(title);
});

return [{
  json: {
    ...srtData,
    ...titleData,
    srt_array: srtArray,
    title_array: titleArray
  }
}];

