const data = $input.first().json;

const files = [
  { fileId: data.welcomeFlowID, fileName: 'Welcome Flow' },
  { fileId: data.cartAbandonFlowID, fileName: 'Cart Abandon Flow' },
  { fileId: data.checkoutAbandonFlowID, fileName: 'Checkout Abandon Flow' },
  { fileId: data.browseAbandonFlowID, fileName: 'Browse Abandon Flow' },
  { fileId: data.postPurchaseFlowID, fileName: 'Post Purchase Flow' },
  { fileId: data.siteAbandonFlowID, fileName: 'Site Abandon Flow' },
  { fileId: data.winBackFlowID, fileName: 'Win-Back Flow' }
];

return files.map(file => ({
  json: file
}));

// Get the folder ID from the previous node (Create folder)
const folderId = $input.first().json.id;

// Get all the files from the Aggregate node's data array
// The aggregate node has combined all the file items into a 'data' array
const files = $('Aggregate').first().json.data;

// Create an array of items with each document ID and the folder ID
const filesToMove = files.map(file => ({
  fileId: file.fileId,
  fileName: file.fileName,
  folderId: folderId
}));

// Return each file as a separate item for the Move file node
return filesToMove.map(file => ({
  json: file
}));

