// Get the input - it might be nested in different ways
let inputData = $input.first()?.json;

// Basic guard
if (!inputData) {
  throw new Error('No input found on $input.first().json');
}

// If the entire response is a string, parse it
if (typeof inputData === 'string') {
  // Remove markdown code blocks if present
  inputData = inputData.replace(/```json\s*/g, '').replace(/```\s*/g, '');
  inputData = JSON.parse(inputData);
}

// The data might be nested - check common patterns
let jsonData;

// Direct concepts
if (inputData.concepts) {
  jsonData = inputData;
}
// Nested in response
else if (inputData.response && typeof inputData.response === 'string') {
  const cleanedResponse = inputData.response.replace(/```json\s*/g, '').replace(/```\s*/g, '');
  if (/^\s*[\[{]/.test(cleanedResponse)) {
    jsonData = JSON.parse(cleanedResponse);
  } else {
    const match = cleanedResponse.match(/\{[\s\S]*\}/);
    if (match) jsonData = JSON.parse(match[0]);
  }
}
// Nested in output
else if (inputData.output && typeof inputData.output === 'string') {
  const cleanedOutput = inputData.output.replace(/```json\s*/g, '').replace(/```\s*/g, '');
  if (/^\s*[\[{]/.test(cleanedOutput)) {
    jsonData = JSON.parse(cleanedOutput);
  } else {
    const match = cleanedOutput.match(/\{[\s\S]*\}/);
    if (match) jsonData = JSON.parse(match[0]);
  }
}
// JSON inside a text field
else if (inputData.text || inputData.message || inputData.content) {
  const textContent = inputData.text || inputData.message || inputData.content;
  const jsonMatch = textContent.match(/\{[\s\S]*\}/);
  if (jsonMatch) jsonData = JSON.parse(jsonMatch[0]);
}
else {
  throw new Error('Could not find concepts in input. Input keys: ' + JSON.stringify(Object.keys(inputData)));
}

// Validate
if (!jsonData || !Array.isArray(jsonData.concepts)) {
  throw new Error('No concepts array found in parsed data');
}

// Build prompts with on-ad copy included
const imagePrompts = jsonData.concepts.map((concept) => {
  const img = concept.imagePrompt || {};
  const headline = concept.headline || '';
  // Accept sloppy upstream key "usbheadline" as a fallback
  const subheadline = concept.subheadline || concept.usbheadline || '';
  const body = concept.bodyCopy || '';
  const cta = concept.cta || '';

  const elementsStr = Array.isArray(img.elements)
    ? img.elements.join(', ')
    : (typeof img.elements === 'string' ? img.elements : '');

  const fullPrompt =
    `Style: ${img.style || 'photorealistic'}. ` +
    `Composition: ${img.composition || ''}. ` +
    `Color palette: ${img.colorPalette || 'brand blue and white'}. ` +
    `Mood: ${img.mood || 'professional, trustworthy'}. ` +
    (elementsStr ? `Include elements: ${elementsStr}. ` : '') +
    `Render the following copy on the ad with clear hierarchy and high legibility. ` +
    (headline ? `Headline: "${headline}". ` : '') +
    (subheadline ? `Subheadline: "${subheadline}". ` : '') +
    (body ? `Body: "${body}". ` : '') +
    (cta ? `CTA button label: "${cta}". ` : '') +
    `Typography: modern sans serif, strong contrast, safe margins. ` +
    `Output sharp at 1080x1080 and 1200x1500. Do not add any extra words beyond the provided copy.`;

  const dallePrompt =
    `${img.style || 'photorealistic'} style. ${img.composition || ''} ` +
    `Professional advertising image with ${img.colorPalette || 'blue and white'} colors. ` +
    `${img.mood || 'professional'} mood. ` +
    (elementsStr ? `Includes ${elementsStr}. ` : '') +
    `Overlay text: Headline "${headline}"` +
    (subheadline ? `, subheadline "${subheadline}"` : '') +
    (body ? `, body "${body}"` : '') +
    (cta ? `, CTA "${cta}"` : '') +
    `.`;

  return {
    json: {
      conceptId: concept.conceptId,
      psychologyFramework: concept.psychologyFramework,
      headline,
      subheadline,
      bodyCopy: body,
      cta,
      imagePrompt: img,          // keep original structured visual spec
      fullPrompt,                // use this for your image model
      dallePrompt                // shorter fallback prompt if needed
    }
  };
});

// Return array of items for n8n to process
return imagePrompts;
