// Generate session ID for memory management
const crypto = require('crypto');
const sessionId = crypto.randomUUID();

// Prepare context for AI agents
const formData = $input.first().json;

// Get strategy document content if available
let strategyDoc = '';
try {
  const docNode = $('Get Strategy Document').first();
  strategyDoc = docNode?.json?.content || '';
} catch (e) {
  // If document retrieval failed, continue without it
  strategyDoc = '';
}

return [{
  json: {
    sessionId: sessionId,
    clientName: formData['Client Name'],
    product: formData['Product/Service Name'],
    price: formData['Price Point'],
    paymentOptions: formData['Payment Options'],
    audience: formData['Target Audience'],
    painPoints: formData['Main Pain Points'],
    uniqueMechanism: formData['Unique Mechanism'],
    transformation: formData['Transformation Promise'],
    clientResults: formData['Client Results'],
    coreOffer: formData['Core Offer Details'],
    bonusStack: formData['Bonus Stack'],
    totalValue: formData['Total Value'],
    style: formData['VSL Style'],
    length: formData['VSL Length'],
    urgency: formData['Urgency Element'],
    guarantee: formData['Guarantee'],
    strategyContext: strategyDoc
  }
}];

// Compile all sections into final VSL script
const hook = $('VSL Hook Creation Agent').first().json.output;
const story = $('VSL Story Architecture Agent').first().json.output;
const objections = $('VSL Objection Handling Agent').first().json.output;
const close = $('Closing CTA Agent').first().json.output;

// Get the original form data directly
const formData = $('VSL Form Submission').first().json;
const context = $('Prepare Context').first().json;

// Create formatted script with metadata - using formData as fallback
const compiledScript = `# ${context.clientName || formData['Client Name']} - VSL Script
## ${context.product || formData['Product/Service Name']}

---

### METADATA
- **Style:** ${context.style || formData['VSL Style']}
- **Target Length:** ${context.length || formData['VSL Length']}
- **Price Point:** $${context.price || formData['Price Point']}
- **Guarantee:** ${context.guarantee || formData['Guarantee']}

---

## OPENING HOOK
${hook}

---

## MAIN STORY & DISCOVERY
${story}

---

## OBJECTION HANDLING
${objections}

---

## CLOSE & CALL TO ACTION
${close}

---

### PRODUCTION NOTES
- Read all [EMOTION] tags before speaking to prepare emotional state
- [PACE] indicators: Slow = 120 wpm, Normal = 150 wpm, Fast = 180 wpm
- [TONE] shifts should be gradual, not jarring
- [ENERGY] builds throughout, peaking at CTA
- Practice transitions between sections for smooth flow

### TIMING ESTIMATE
- Hook: 30-60 seconds
- Story: ${(context.length || formData['VSL Length']) === '5-7 Minutes' ? '2-3 minutes' : (context.length || formData['VSL Length']) === '10-15 Minutes' ? '5-7 minutes' : '10-15 minutes'}
- Objections: ${(context.length || formData['VSL Length']) === '5-7 Minutes' ? '1-2 minutes' : (context.length || formData['VSL Length']) === '10-15 Minutes' ? '3-4 minutes' : '5-7 minutes'}
- Close: ${(context.length || formData['VSL Length']) === '5-7 Minutes' ? '1-2 minutes' : (context.length || formData['VSL Length']) === '10-15 Minutes' ? '2-3 minutes' : '3-5 minutes'}
`;

return [{
  json: {
    clientName: context.clientName || formData['Client Name'],
    product: context.product || formData['Product/Service Name'],
    vslScript: compiledScript,
    sessionId: context.sessionId || 'no-session'
  }
}];

