// In a Code node before the Simple Memory node
const crypto = require('crypto'); // Import the crypto module

// Generate a UUID (Universally Unique Identifier)
const sessionId = crypto.randomUUID();

// Set the sessionId in the output data for the next node
return [{ json: { sessionId: sessionId } }];

const docs = [
  { url: $input.item.json.webinar_script_doc, name: 'Webinar Script' },
  { url: $input.item.json.email_sequence_doc, name: 'Email Sequence' },
  { url: $input.item.json.slide_deck_doc, name: 'Slide Deck' },
  { url: $input.item.json.registration_page_doc, name: 'Registration Page' },
  { url: $input.item.json.thank_you_page_doc, name: 'Thank You Page' }
];

return docs.map(doc => ({
  json: {
    docId: doc.url.split('/d/')[1].split('/')[0],
    docUrl: doc.url,
    docName: doc.name,
    clientName: $input.item.json.clientName,
    folderId: $input.item.json.folderId // Pass the folder ID through
  }
}));
